use RecipeOrganize
go
drop database RecipeOrganize
create database RecipeOrganize
CREATE TABLE account (
 
  userID INT IDENTITY(1,1) PRIMARY KEY,
  userName VARCHAR(30) UNIQUE NOT NULL,
  password VARCHAR(30) NOT NULL,
  fullName VARCHAR(30) NOT NULL,
  phone VARCHAR(12) NULL,
  status INT NOT NULL,
  role bit NOT NULL,
  token NVARCHAR(50) NOT NULL,
  email VARCHAR(100) UNIQUE NOT NULL,
  image_path VARCHAR(255)
);

CREATE TABLE category (
  categoryID INT PRIMARY KEY,
  categoryName VARCHAR(100) NOT NULL
);

CREATE TABLE recipe (
  recipeID INT PRIMARY KEY NOT NULL,
  recipeName VARCHAR(50) NOT NULL,
  caloRecipe FLOAT NULL,
  description TEXT NULL,
  imgUrl VARCHAR(50) NULL,
  avgRating INT NOT NULL,
  difficulty VARCHAR(50) NULL,
  ingredient_table TEXT NULL,
  categoryID INT,
  FOREIGN KEY (categoryID) REFERENCES category(categoryID)
);

CREATE TABLE favorite (
  favoriteID INT PRIMARY KEY NOT NULL,
  userID INT,
  recipeID INT,
  FOREIGN KEY (userID) REFERENCES account(userID),
  FOREIGN KEY (recipeID) REFERENCES recipe(recipeID)
);

CREATE TABLE mealPlan (
  planID INT IDENTITY(1,1) PRIMARY KEY,
  planDate DATE NULL,
  userID INT,
  recipeID INT,
  FOREIGN KEY (userID) REFERENCES account(userID),
  FOREIGN KEY (recipeID) REFERENCES recipe(recipeID)
);

CREATE TABLE feedback (
  feedbackID INT IDENTITY(1,1) PRIMARY KEY,
  rating INT NULL,
  comment TEXT NULL,
  feedbackDate DATE NULL,
  userName VARCHAR(30)NOT NULL,
  recipeID INT,
  FOREIGN KEY (userName) REFERENCES account(userName),
  FOREIGN KEY (recipeID) REFERENCES recipe(recipeID)
);

CREATE TABLE ingredient (
  ingredientID INT PRIMARY KEY NOT NULL,
  ingredientName VARCHAR(50) NULL,
  caloIn VARCHAR(50) NULL,
  quantity VARCHAR(50) NULL,
  unit VARCHAR(50) NULL,
  recipeID INT,
  FOREIGN KEY (recipeID) REFERENCES recipe(recipeID)
);

CREATE TABLE step (
  stepID INT PRIMARY KEY,
  descriptionName TEXT NULL,
  recipeID INT,
  FOREIGN KEY (recipeID) REFERENCES recipe(recipeID)
);
